import axios from 'axios';
import React, { useState, useEffect } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom';

const AddAction = () => {
    let navigate = useNavigate();
    const[cat,setSelectedCategory]=useState([])
    const [tasks, setTask] = useState([])
    const [action, setAction] = useState("");
    const { wid, tid } = useParams();
    // console.log(wid);
    // console.log(tid);

    useEffect(() => {
        loadUsers();

    }, []
    );
    const loadUsers = async () => {
        const result = await axios.get(`http://localhost:9191/task/fetchTaskList/${wid}`);
        setTask(result.data);

    }
    var bodyFormData = new FormData();
    bodyFormData.append('name', action);
    bosy
    const onSubmit = async (e) => {
        axios({
            method: "post",
            url: `http://localhost:9191/action/addAction/${tid}`,
            data: bodyFormData,
            headers: { "Content-Type": "multipart/form-data" },
        })
            .then(function (response) {
                //handle success
                console.log(response);
            })
            .catch(function (response) {
                //handle error
                console.log(response);
            });
        navigate("/");
    }


    return (
        <div className='admincreate'>
            <div className='container'>
                <div className='col-md-6 offset-md-3 border rounded p-4 mt-2 shadow'>
                    <form onSubmit={(e) => onSubmit(e)}>
                        <div className='mb-3'>
                            <label htmlFor='Name' className='form-label'>
                                Action Name
                            </label>
                            <input
                                type={"text"}
                                className="form-control"
                                placeholder='Enter Worklfow Name'
                                name="wf_name"
                                //value="wf_name"
                                onChange={(e) => setAction(e.target.value)}
                                required
                            />
                        </div>
                        <div className='mb-3'>
                            Select Next Actions
                            <select
                                //value={selectedCategory}
                                onChange={(e) => setSelectedCategory(e.target.value)}
                                className="product-dropdown"
                                name="product-dropdown"
                            >
                                {tasks.map((task) => (
                                    <option value={task.taskId}>{task.taskId}</option>
                                ))}
                            </select>
                        </div>

                        <button type='submit' className='btn btn-outline-primary'>Submit</button>
                        <Link className='btn btn-outline-danger mx-2' to="/">Cancel</Link>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default AddAction
