import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import Table from 'react-bootstrap/Table';

export default function Tasks() {
    const[tasks,setTask]=useState([])

    const [data,setData]=useState([])

    const handleClick=()=>{
        setData([...data,{fname:"",lname:""}])
    }
    const handleChange=(e,i)=>{
        const {name,value}=e.target
        const onchangeVal = [...data]
        onchangeVal[i][name]=value
        setData(onchangeVal)
    }

    const{id}=useParams();
    useEffect(()=>{
        loadUser();
    },[]);

    const loadUser=async()=>{
        const result=await axios.get(`http://localhost:9191/taskInstance/fetchTaskInstances/1`)
        setTask(result.data)
    }


  return (
    <div className='container'>
    <div className='row'>
        {/* <div className='col-md-6 offset-md-3 border rounded p-4 mt-2 shadow'> */}
        <div className='py-4'>
                <Table striped bordered hover >
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Comment</th>
                            <th scope="col">Attchments</th>
                            <th scope="col">Status</th>
                            <th scope="col">Desc</th>
                            <th scope="col">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tasks.map((task, index) => (

                                <tr>
                                    <th scope="row" key={index}>{index + 1}</th>
                                    <td>{task.comments}</td>
                                    <td>{task.attachments}</td>
                                    <td>{task.status}</td>
                                    <td>{task.workflowInstance.description}</td>
                                    {/* <td>{task.workflowInstance.workflow.name}</td> */}
                                    <td>
                                    <div className="App">
                                        <button onClick={handleClick}>Add</button>
                                        {
                                            data.map((val,i)=>
                                            <div>
                                                <input name="fname" value={val.fname} onChange={(e)=>handleChange(e,i)} />
                                                <input name="lname" value={val.lname} onChange={(e)=>handleChange(e,i)} />
                                            </div>
                                            )
                                        }
                                        <p>{JSON.stringify(data)}</p>
                                    </div>
                                        {/* <Link className="btn btn-primary mx-2" to={`/usermain`}>
                                            Action
                                        </Link> */}

                                    </td>
                                </tr>
                            )
                            )
                        }
                    </tbody>
                </Table>

            </div>

            <Link className='btn' to={"/usermain"}>Back to Home</Link>
        </div>
        {/* </div> */}
        <div className="footer">
                <p>Made with ❤️ and 🧑‍💻 by <i>Harsh Tripathi</i></p>
            </div>
        </div>
  )
}
